import React, { useEffect, useState } from "react";
import $ from 'jquery';
import { Form } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import Tags from "../../../../node_modules/bootstrap5-tags/tags";
import IonRangeSlider from 'react-ion-slider'

import HobbyList from "./util/hobbyList";

const querystring = require('querystring');

// Language
// Basic: Gender(多元), Date of birth (星座), City & Country (Can set Undefined)
// Introduction questions: 3 (Facts about me)
// Hobby
// Topic you are Interest
// Friends preference: Gender, Age range, Language

const ProfileSettings = (props) => {
    const [user, setUser] = useState(props.user)
    const [isLoaded, setLoaded] = useState(false)   // Check if page is loaded

    const [ulang, setULang] = useState('');
    const [uco, setUCO] = useState('');
    const [ugender, setUGender] = useState('');
    const [dob, setDOB] = useState('');
    const [uhobbies , setUHobbies] = useState([]);
    const [bio , setBio] = useState('');
    const [hashtags , setHashtags] = useState([]);
    const [plang , setPLang ] = useState([]);
    const [pgender , setPGender ] = useState([]);
    // const [ageFrom, setAgeFrom] = useState();
    // const [ageTo, setAgeTo] = useState();
    var ageFrom, ageTo;

    const ageArr = Array.from({length: 53}, (_, i) => i + 12)
    ageArr.push('65+')

    Tags.init("select[multiple]");
    // Other form setting by jquery
    $('.react-datepicker-wrapper').addClass('w-100')
    $('.form-control.dropdown').children('div').addClass('cursor-text')
    
    // Run when form is changed
    const [count, setCount] = useState(0)
    useEffect(()=> {
        setCount(count+1)
        if (!isLoaded) {
            if(count == 1)
                setLoaded(true);
            return
        }
        
        console.log("Form has changed: ")
        props.setFormChanged(true)
        $("#submitBtn").removeClass('disabled')
    }, [ulang, uco, ugender, dob, uhobbies, bio, hashtags, plang, pgender]);

    const sendUpdatePreferenceRequest = async () => {
        // let url = '/user/profile/update';

        let body = querystring.stringify({
            ulang: ulang,
            uco: uco,
            ugender: ugender,
            dob: dob,
            uhobbies: uhobbies,
            bio: bio,
            hashtags: hashtags,
            plang: plang,
            pgender: pgender,
            ageFrom: ageFrom,
            ageTo: ageTo
        })

        console.log(body)

        // let res = await fetch(url, {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        //     body: querystring.stringify({
        //         name: uname,
        //         password: password,
        //         newPassword: newPassword
        //     })
        // });

        // let data
        // try {
        //     data = await res.json();
        // } catch (error) {
        //     props.setAlert({
        //         visible: true,
        //         strongMsg: 'Error!',
        //         msg: `Unexpected error. (${error.message})`
        //     })
        //     return;
        // }

        // if (!data.success) {
        //     props.setAlert({
        //         visible: true,
        //         strongMsg: 'Sorry!',
        //         msg: `${data.message}`
        //     })
        //     return;
        // }

        // setUser(data.user)
        // props.setUser(data.user)
        // window.alert("Your information is updated")
        // resetForm()
    };

    const resetForm = () => {
        $('#submitBtn').addClass('disabled').promise().then("class added")
        // setEmail(user.email)
        // setUName(user.name)
        // setPassword('')
        // setNewPassword('')
    }

    return (
        <>
        
        <form className="forms-sample" onSubmit={async (e)=>{
                e.preventDefault();
                sendUpdatePreferenceRequest()
                return false;
            }}> 

        <div className="card-body">
            <h4 className="card-title">Profile</h4>
            <p className="card-description">Basic Information</p>

            <div className="row">
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label for="ulang" className="col-sm-3 col-form-label">Language</label>
                    <div className="col-sm-9">
                        <select className="form-control" name="ulang" id="ulang" 
                            onChange={(e)=>{
                                let values = e.target.selectedOptions[0].value
                                setULang(values)
                            }}>
                            <option>Please select</option>
                            <option value="yue">Cantonese</option>
                            <option value="cmn">Mandarin</option>
                            <option value="eng">English</option>
                        </select>
                    </div>
                    </Form.Group>
                </div>
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label for="uco" className="col-sm-3 col-form-label">Country of Origin</label>
                    <div className="col-sm-9">
                        <select className="form-control" name="uco" id="uco">
                            <option>Not Declare</option>
                            <option value="CN">China</option>
                            <option value="HK">Hong Kong</option>
                            <option value="IN">India</option>
                            <option value="ID">Indonesia</option>
                            <option value="JP">Japan</option>
                            <option value="KR">South Korea</option>
                            <option value="MY">Malaysia</option>
                            <option value="PH">Philippines</option>
                            <option value="TW">Taiwan</option>
                            <option value="TH">Thailand</option>
                            <option value="VN">Vietnam</option>
                            <optgroup label="Others">
                                <option value="NA">North America</option>
                                <option value="SA">South America</option>
                                <option value="ER">Europe</option>
                                <option value="AS">Asia</option>
                                <option value="AU">Australia</option>
                                <option value="AF">Africa</option>
                            </optgroup>
                        </select>
                    </div>
                    </Form.Group>
                </div>
            </div>

            <div className="row">
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label for="ugender" className="col-sm-3 col-form-label">Gender</label>
                    <div className="col-sm-9">
                        <select className="form-control" name="ugender" id="ugender">
                            <option value="M">Male</option>
                            <option value="F">Female</option>
                            <option value="TM">Trans male</option>
                            <option value="TF">Trans female</option>
                            <option value="NB">Non-binary</option>
                            <option value="ND" selected>Not Declare</option>
                        </select>
                    </div>
                    </Form.Group>
                </div>
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label for="udob" className="col-sm-3 col-form-label">Birth</label>
                    <div className="col-sm-9">
                    <DatePicker id="udob" className="form-control" size="lg"
                        selected={dob}
                        onChange={setDOB}
                    />
                    </div>
                    </Form.Group>
                </div>
            </div>
        </div>

        <div className="card-body">
            <p className="card-description">Tell us more about you</p>
            <div className="form-group row">
                <label for="uhobbies" className="col-sm-3 col-form-label">Hobbies</label>
                <div className="col-sm-9">
                    <select
                        class="form-control"
                        name="uhobbies[]"
                        id="uhobbies"
                        multiple
                        data-allow-new="true"
                        data-allow-clear="1"
                        onChange={(e)=>{
                            let values = Array.from(e.target.selectedOptions, option => option.value);
                            setUHobbies(values)
                        }}>
                        <HobbyList />
                    </select>

                </div>
            </div>

            <div className="form-group row">
                <label for="bio" className="col-sm-3 col-form-label">Bio</label>
                <div className="col-sm-9">
                    <textarea class="form-control" id="bio" rows="5" value={bio} onChange={(e)=>setBio(e.target.value)}></textarea>
                </div>
            </div>

            <div className="form-group row">
                <label for="hashtags" className="col-sm-3 col-form-label">Hashtags</label>
                <div className="col-sm-9">
                    <select
                        class="form-select"
                        name="hashtags[]"
                        id="hashtags"
                        multiple
                        data-allow-new="true"
                        data-allow-clear="1"
                        onChange={(e)=>{
                            let values = Array.from(e.target.selectedOptions, option => option.value);
                            setHashtags(values)
                        }}>
                    </select>

                </div>
            </div>
        </div>


        <div className="card-body">
            <p className="card-description">What types of people you would like to meet?</p>

                <Form.Group className="row">
                    <label for="plang" className="col-sm-3 col-form-label">Language</label>
                    <div className="col-sm-9">
                        <select
                            class="form-control"
                            name="plang[]"
                            id="plang"
                            multiple
                            data-allow-clear="1"
                            data-suggestions-threshold="0"
                            onChange={(e)=>{
                                let values = Array.from(e.target.selectedOptions, option => option.value);
                                setPLang(values)
                                console.log(values)
                            }}>
                            <option value="yue" selected>Cantonese</option>
                            <option value="cmn" selected>Mandarin</option>
                            <option value="eng" selected>English</option>
                        </select>
                    </div>
                </Form.Group>

                <Form.Group className="row">
                    <label for="pgender" className="col-sm-3 col-form-label">Gender</label>
                    <div className="col-sm-9">
                        <select
                            class="form-control"
                            name="pgender[]"
                            id="pgender"
                            multiple
                            data-allow-clear="1"
                            data-suggestions-threshold="0"
                            onChange={(e)=>{
                                let values = Array.from(e.target.selectedOptions, option => option.value);
                                setPGender(values)
                                console.log(values)
                            }}>
                            <option value="M" selected>Male</option>
                            <option value="F" selected>Female</option>
                            <option value="TM" selected>Trans male</option>
                            <option value="TF" selected>Trans female</option>
                            <option value="NB" selected>Non-binary</option>
                            <option value="ND" selected>Not Declare</option>
                        </select>
                    </div>
                </Form.Group>
                
                
                <Form.Group className="row">
                    <label className="col-sm-3 col-form-label">Age Range</label>
                    <div className="col-sm-9">
                        <IonRangeSlider type="double" 
                            // min="12" max="50" 
                            values={ageArr}
                            from={18-ageArr[0]} to={40-ageArr[0]}
                            onChange={(e)=>{
                                ageFrom = $('.irs-from')[0].innerHTML;
                                ageTo = $('.irs-to')[0].innerHTML;
                                // setAgeFrom($('.irs-from')[0].innerHTML)
                                // setAgeTo($('.irs-to')[0].innerHTML)
                            }} />
                    </div>
                </Form.Group>

            <div class="text-right">
            <button type="submit" id="submitBtn" className="btn btn-gradient-primary mr-2 disabled" onClick={(e)=>{
                if (e.target.classList.contains('disabled'))
                    e.preventDefault();
            }}>Save Change</button>
            <button type="reset" className="btn btn-light" onClick={(e)=>{e.preventDefault(); resetForm();}}>Reset</button>
            </div>
            
        </div>
        </form>
        </>
    )
}

export default ProfileSettings;
