import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Form } from 'react-bootstrap';
import LoginBox from "../component/login/LoginBox";
import LoginVerify from "../component/login/LoginVerify";
import Signup from "../component/login/Signup";

export class Login extends Component {
  render() {
    const [loginState, setloginState] = useState('login');
    const [userName, setUserName] = useState('');
    const [userPassword, setUserPassword] = useState('');

    let navigate = useNavigate();

    useEffect(() => {
        console.log(`changed to ${loginState}`)
        if (loginState === 'success') {
            navigate("/home");
        }
    }, [loginState]);

    const sendLoginRequest = async () => {
        let url = '/user/login';
        let res = await fetch(url, {
            method: 'POST',
            body: JSON.stringify({
                "email": userName,
                "password": userPassword
            })
        });
        let data = await res.json();
        console.log(data);
        if (data.success) {
            setloginState('verify');
            //setloginState('success');
        }
    };

    let loginplaceholder = 0;
    if (loginState === 'login') {
        loginplaceholder = <LoginBox setUserName={setUserName} setUserPassword={setUserPassword} sendLoginRequest={sendLoginRequest} setloginState={setloginState} />;
    }

    if (loginState === 'verify') {
        loginplaceholder = <LoginVerify setloginState={setloginState}/>;
    }

    if (loginState === 'signup') {
        loginplaceholder = <Signup setloginState={setloginState} />;
    }

    return (
      <div>
        <div className='d-flex align-items-center auth p-5'>
          <div className="row w-100 mx-0">
            <div className="col-md-7 grid-margin">
              <div className="p-5">
                <h2>Welcome!</h2>
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                <p>Use Frends-HK to meet new friends!</p>
              </div>
            </div>
            <div className="col-md-5 grid-margin stretch-card">
              <div className="card">
                <div className="card-body">
                  <h4 className="card-title">Headings with secondary text</h4>
                  <p className="card-description"> Add faded secondary text to headings </p>
                  <div className="template-demo">
                    <h1> h1. Heading <small className="text-muted"> Secondary text </small>
                    </h1>
                    <h2> h2. Heading <small className="text-muted"> Secondary text </small>
                    </h2>
                    <h3> h3. Heading <small className="text-muted"> Secondary text </small>
                    </h3>
                    <h4> h4. Heading <small className="text-muted"> Secondary text </small>
                    </h4>
                    <h5> h5. Heading <small className="text-muted"> Secondary text </small>
                    </h5>
                    <h6> h6. Heading <small className="text-muted"> Secondary text </small>
                    </h6>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      // <div>
      //   <div className="d-flex align-items-center auth px-0">
      //     <div className="row w-100 mx-0">
      //       <div className="col-lg-4 mx-auto">
      //         <div className="auth-form-light text-left py-5 px-4 px-sm-5">

      //         </div>
      //         <div className="auth-form-light text-left py-5 px-4 px-sm-5">
      //           <div className="brand-logo">
      //             <img src={require("../../assets/images/logo.svg")} alt="logo" />
      //           </div>
      //           <h4>Hello! let's get started</h4>
      //           <h6 className="font-weight-light">Sign in to continue.</h6>
      //           <Form className="pt-3">
      //             <Form.Group className="d-flex search-field">
      //               <Form.Control type="email" placeholder="Username" size="lg" className="h-auto" />
      //             </Form.Group>
      //             <Form.Group className="d-flex search-field">
      //               <Form.Control type="password" placeholder="Password" size="lg" className="h-auto" />
      //             </Form.Group>
      //             <div className="mt-3">
      //               <Link className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" to="/dashboard">SIGN IN</Link>
      //             </div>
      //             <div className="my-2 d-flex justify-content-between align-items-center">
      //               <div className="form-check">
      //                 <label className="form-check-label text-muted">
      //                   <input type="checkbox" className="form-check-input"/>
      //                   <i className="input-helper"></i>
      //                   Keep me signed in
      //                 </label>
      //               </div>
      //               <a href="!#" onClick={event => event.preventDefault()} className="auth-link text-black">Forgot password?</a>
      //             </div>
      //             <div className="mb-2">
      //               <button type="button" className="btn btn-block btn-facebook auth-form-btn">
      //                 <i className="mdi mdi-facebook mr-2"></i>Connect using facebook
      //               </button>
      //             </div>
      //             <div className="text-center mt-4 font-weight-light">
      //               Don't have an account? <Link to="/user/register" className="text-primary">Create</Link>
      //             </div>
      //           </Form>
      //         </div>
      //       </div>
      //     </div>
      //   </div>  
      // </div>
    )
  }
}

export default Login
