import React, { useState } from "react";
import UploadPicture from "./uploadPicture";
const querystring = require('querystring');

const AccountSettings = (props) => {
    let user = props.user

    var initUname = user.name;

    const sendResetPwRequest = async () => {
        let url = '/user/forgotPassword/reset';

        let res = await fetch(url, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: querystring.stringify({
                email: 'email',
                code: 'verifyCode',
                password: 'password'
            })
        });

        let data
        try {
            data = await res.json();
        } catch (error) {
            props.setAlert({
                visible: true,
                strongMsg: 'Error!',
                msg: `Unexpected error. (${error.message})`
            })
            return;
        }
    };

    return (
        <>
            <h4 className="card-title" onLoad={()=>{
                document.getElementById("nameInput").value = user.name;
                console.log(123)
            }}>Account</h4>

            <UploadPicture user={user} setUser={props.setUser} setAlert={props.setAlert} />

            <form className="forms-sample" onSubmit={(e)=>{
                e.preventDefault();
                props.setAlert({visible:true});
                // return false;
            }}> 

                <div className="form-group">
                    <label for="emailInput">Email Address</label>
                    <input placeholder="Email Address" type="email" id="emailInput" className="form-control form-control-lg" readOnly value={user==null?'':user.email} />
                </div>
                <div className="form-group">
                    <label for="nameInput">Display Name</label>
                    <input placeholder="Display Name" type="text" id="nameInput" className="form-control form-control" onChange={(e) => {
                        user.name = e.target.value
                    }} />
                </div>
                <div className="form-group">
                    <label for="passwordInput">Password</label>
                    <input placeholder="Email" type="password" id="passwordInput" className="form-control form-control" />
                </div>
                <div className="form-group">
                    <label for="newPasswordInput">New Password</label>
                    <input placeholder="Password" type="password" id="newPasswordInput" className="form-control form-control" />
                </div>
                <div className="form-check">
                    <label className="form-check-label text-muted">
                        <input type="checkbox" className="form-check-input" />
                        <i className="input-helper"></i>Remember me
                    </label>
                </div>
                <button type="submit" className="btn btn-gradient-primary mr-2">Submit</button>
                <button className="btn btn-light">Cancel</button>
            </form>
        </>
    )
}

export default AccountSettings;
