import React, { useState } from "react";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";
// import Cropper from "https://unpkg.com/cropperjs";
// import "https://unpkg.com/cropperjs/dist/cropper.css"

const querystring = require('querystring');

const UploadPicture = (props) => {
    let user = props.user

    var reader;
    const [imageSrc, setImageSrc] = useState("")

    const showModal = () => {
        // cropper = new Cropper(image, {
        //     aspectRatio: 1,
        //     viewMode: 3,
        //     preview:'.preview'
        // });
    }

    return (
        <>
            <div class="image_area">
                <form method="post">
                    <label for="upload_image">
                        <img src={(user==null||user.picture==null)? require("../../../assets/images/emptyFace.png") : props.user.picture} id="uploaded_image" class="img-responsive rounded-circle" width="200px" height="200px" />
                        <div class="overlay">
                            <div class="text">Click to Change Profile Image</div>
                        </div>
                        <input type="file" name="image" class="image" id="upload_image" onChange={(e) => {
                            var files = e.target.files;
                            var done = (url) => {
                                setImageSrc(url);
                                // var m = window.document.getElementById("modal")
                                window.document.getElementById("showModalBtn").click();
                            }

                            if(files && files.length > 0) {
                                reader = new FileReader();
                                reader.onload = function(event) {   
                                    done(reader.result);
                                };
                                reader.readAsDataURL(files[0]);
                            }
                        }} />
                    </label>
                </form>
            </div>

            <div class="modal fade" id="modal" tabindex="-1" role="dialog" aria-labelledby="modalLabel" aria-hidden="true">
			  	<div class="modal-dialog modal-dialog-centered" role="document">
			    	<div class="modal-content">
			      		<div class="modal-header">
			        		<h5 class="modal-title">Crop Image Before Upload</h5>
			        		<button type="button" class="close" data-dismiss="modal" aria-label="Close">
			          			<span aria-hidden="true">&times;</span>
			        		</button>
			      		</div>
			      		<div class="modal-body">
			        		<div class="img-container">
			            		<div class="row">
			                		<div class="col-md-8">
			                    		<img src={imageSrc} id="sample_image" />
			                		</div>
			                		<div class="col-md-4">
			                    		<div class="preview"></div>
			                		</div>
			            		</div>
			        		</div>
			      		</div>
			      		<div class="modal-footer">
			      			<button type="button" id="crop" class="btn btn-primary">Crop</button>
			        		<button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
			      		</div>
			    	</div>
			  	</div>
			</div>
            <input id="showModalBtn" type="hidden" data-toggle="modal" data-target="#modal" onClick={()=>{showModal()}} />
        </>
    )
}

export default UploadPicture;
