import React, { useEffect, useState } from 'react';
import { useHistory, Link } from "react-router-dom";
import { Form } from 'react-bootstrap';
import LoginBox from "../component/login/LoginBox";
import LoginByPassword from "../component/login/LoginByPassword";
import LoginVerify from "../component/login/LoginVerify";
import Signup from "../component/login/Signup";
import AlertMessage from '../component/common/AlertMessage';

const Login = () => {
    const [loginState, setloginState] = useState('login');
    const [userName, setUserName] = useState('');
    const [userPassword, setUserPassword] = useState('');

    const [alert, setAlert] = useState('');

    let navigate = useHistory();

    useEffect(() => {
        console.log(`changed to ${loginState}`)
        if (loginState === 'success') {
            navigate("/home");
        }
    }, [loginState]);

    const sendVerifyEmail = async () => {
      console.log(userName)

      let url = '/user/login/email';
      let res = await fetch(url, {
          method: 'POST',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          body: `email=${userName}`,
      });

      let data = await res.json();

      if (!data.success) {
        setAlert({
          visible: true,
          strongMsg: 'Error!',
          msg: data.message
        })
      }
    }

    let loginplaceholder = 0;
    if (loginState === 'login') {
        loginplaceholder = <LoginBox setUserName={setUserName} setloginState={setloginState} />;
    }

    if (loginState === 'password') {
      console.log(userName)
      loginplaceholder = <LoginByPassword userName={userName} setUserPassword={setUserPassword} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail} />;
    }

    if (loginState === 'verify') {
        loginplaceholder = <LoginVerify userName={userName} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail}/>;
    }

    if (loginState === 'signup') {
        loginplaceholder = <Signup setloginState={setloginState} />;
    }

    return (
      <div>
        <div className='d-flex align-items-center auth p-5'>
          <div className="row w-100 mx-0">
            <div className="col-md-7 grid-margin">
              <div className="pt-5 pl-5 pr-5">
                <h2>Welcome!</h2>
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                <div>Use Frends-HK to meet new friends!</div>
              </div>
            </div>
            <div className="col-md-5 grid-margin stretch-card">
              <div className="card">
                <div className="card-body">
                  {alert.visible === true && <AlertMessage strongMsg={alert.strongMsg} msg={alert.msg} />}
                  <h4 className="card-title">Hello! let's get started</h4>
                  {loginplaceholder}

                  <div className="my-2 d-flex justify-content-between align-items-center">
                      <div className="form-check">
                          <label className="form-check-label text-muted">
                              <input type="checkbox" className="form-check-input" />
                              <i className="input-helper"></i>
                              Keep me signed in
                          </label>
                      </div>
                      <a href="!#" className="auth-link text-black">Forgot password?</a>
                  </div>
                  <div className="text-center mt-4 font-weight-light">
                      Don't have an account? <button type="button" className="btn btn-link text-primary" onClick={() => { setloginState('signup'); }}>Create</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Login
