import React, { useEffect, useState } from 'react';
import { useHistory, Link } from "react-router-dom";
import LoginBox from "../component/login/LoginBox";
import LoginByPassword from "../component/login/LoginByPassword";
import LoginVerify from "../component/login/LoginVerify";
import AlertMessage from '../component/common/AlertMessage';
import ForgotPassword from '../component/login/ForgotPassword';
import ResetPasswordSuccess from '../component/login/ResetPasswordSuccess';
const querystring = require('querystring');

const Login = () => {
    const [loginState, setloginState] = useState('login');
    const [userName, setUserName] = useState('');
    const [userPassword, setUserPassword] = useState('');

    const [alert, setAlert] = useState('');

    const [user, setUser] = useState(null);

    let navigate = useHistory();

    useEffect(() => {
        console.log(`changed to ${loginState}`)
        if (loginState === 'success') {
            navigate("/home");
            window.sessionStorage.setItem('UserProfile', user);
        }
    }, [loginState]);

    const sendVerifyEmail = async () => {
      let url = '/user/login/email';
      let res = await fetch(url, {
          method: 'POST',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          body: querystring.stringify({
            email: userName
          })
      });

      let data;
      try {
        data = await res.json();
      } catch (error) {
        setAlert({
          visible: true,
          strongMsg: 'Error!',
          msg: `Unexpected error. (${error.message})`
        })
        return;
      }
      
      console.log(data)

      if (!data.success) {
        setAlert({
          visible: true,
          strongMsg: 'Sorry!',
          msg: data.message
        })
      } else {
        console.log("Email sent")
      }
    }

    const alreadyLoggedin = async () => {
      // Check session
      let url = '/checkSession';
      let res = await fetch(url, {
          method: 'GET',
      });

      let data;
      try {
          data = await res.json();
      } catch (error) {
          setAlert({
              visible: true,
              strongMsg: 'Error!',
              msg: `Unexpected error. (${error.message})`
          })
          return false
      }

      const loginVerification = data.verification;
      if (loginVerification) {
          if (loginVerification.email === userName && loginVerification.verified) {
              return true
          }
      }

      return false
    }

    let loginplaceholder = 0;
    if (loginState === 'login') {
        loginplaceholder = <LoginBox setUserName={setUserName} setloginState={setloginState} setAlert={setAlert} setUser={setUser} alreadyLoggedin={alreadyLoggedin} />;
    }

    if (loginState === 'password') {
      console.log(userName)
      loginplaceholder = <LoginByPassword userName={userName} userPassword={userPassword} setUserPassword={setUserPassword} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail} setAlert={setAlert} setUser={setUser} alreadyLoggedin={alreadyLoggedin} />;
    }

    if (loginState === 'verify') {
        loginplaceholder = <LoginVerify userName={userName} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail} setAlert={setAlert}setUser={setUser}  alreadyLoggedin={alreadyLoggedin} />;
    }

    if (loginState === 'forgot') {
      loginplaceholder = <ForgotPassword userName={userName} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail} setAlert={setAlert} alreadyLoggedin={alreadyLoggedin} />;
    }

    if (loginState === 'resetPwAlready') {
      loginplaceholder = <ResetPasswordSuccess setloginState={setloginState} setAlert={setAlert} />;
    }

    return (
      <div>
        <div className='d-flex align-items-center auth p-5 make-friends-background'>
          <div className="row w-100 mx-0">
            <div className="col-md-7 grid-margin">
              <div className="pt-5 pl-5 pr-5">
                <h1>Welcome!</h1>
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                <div>Let's meet friends in Frends-HK!</div>
              </div>
            </div>
            <div className="col-md-5 grid-margin stretch-card">
              <div>
              {alert.visible === true && <AlertMessage strongMsg={alert.strongMsg} msg={alert.msg} />}
              <div className="card">
                <div className="card-body">
                  {loginplaceholder}
                  <div className="text-center mt-4 font-weight-light">
                    Don't have an account? <a href='/register' type="button" className="btn btn-link text-primary">Create</a>
                </div>
                </div>
              </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Login
