import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
const querystring = require('querystring');

const Verify = () => {
    var errorMessage;

    const verifyUser = async () => {
        let urlData = window.location.search;
        let queryData = new URLSearchParams(urlData);
        let m = queryData.get('m');
        let id = queryData.get('id') + '1';
        console.log("m:"+m)
        console.log(id)

        let url = '/user/activate' + urlData + '1'
        let res = await fetch(url);
        
        let data
        try {
            data = await res.json();
        } catch (error) {
            errorMessage = `Unknown error. (${error.message})`;
        }

        console.log(JSON.stringify(data))

        if (!data.success) {
            errorMessage = data.message;
        }
    }

    return (
        <div onLoad={verifyUser()}>
          <div className="d-flex align-items-center auth px-0 make-friends-background">
            <div className="row w-100 mx-0">
              <div className="col-lg-4 mx-auto">
                <div className="auth-form-light text-left py-5 px-4 px-sm-5">
                  
                  <div className="brand-logo">
                    <img src={require("../../assets/images/logo.svg")} alt="logo" />
                  </div>
                  <div className="text-center">
            <h1>Success!</h1>
            <p>We have sent an email for your account verification.<br/>Please check your registered email inbox.</p>
            <div className="text-center mt-4 font-weight-light">
                Back to <Link to="/login" className="text-primary">Login</Link> page
            </div>
        </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
}

export default Verify
