import React, { useState } from "react";
const querystring = require('querystring');


const AccountSettings = (props) => {

    $(document).ready(function(){

        var $modal = $('#modal');
    
        var image = document.getElementById('sample_image');
    
        var cropper;
    
        $('#upload_image').change(function(event){
            var files = event.target.files;
    
            var done = function(url){
                image.src = url;
                $modal.modal('show');
            };
    
            if(files && files.length > 0)
            {
                reader = new FileReader();
                reader.onload = function(event)
                {
                    done(reader.result);
                };
                reader.readAsDataURL(files[0]);
            }
        });
    
        $modal.on('shown.bs.modal', function() {
            cropper = new Cropper(image, {
                aspectRatio: 1,
                viewMode: 3,
                preview:'.preview'
            });
        }).on('hidden.bs.modal', function(){
            cropper.destroy();
               cropper = null;
        });
    
        $('#crop').click(function(){
            canvas = cropper.getCroppedCanvas({
                width:400,
                height:400
            });
    
            canvas.toBlob(function(blob){
                url = URL.createObjectURL(blob);
                var reader = new FileReader();
                reader.readAsDataURL(blob);
                reader.onloadend = function(){
                    var base64data = reader.result;
                    $.ajax({
                        url:'upload.php',
                        method:'POST',
                        data:{image:base64data},
                        success:function(data)
                        {
                            $modal.modal('hide');
                            $('#uploaded_image').attr('src', data);
                        }
                    });
                };
            });
        });
        
    });

    let user = props.user
    console.log("AccountSettings:")
    console.log(user)

    const sendResetPwRequest = async () => {
        let url = '/user/forgotPassword/reset';

        let res = await fetch(url, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: querystring.stringify({
                email: 'email',
                code: 'verifyCode',
                password: 'password'
            })
        });

        let data
        try {
            data = await res.json();
        } catch (error) {
            props.setAlert({
                visible: true,
                strongMsg: 'Error!',
                msg: `Unexpected error. (${error.message})`
            })
            return;
        }
    };

    return (
        <>
            <h4 className="card-title">Account</h4>
            <form className="forms-sample" onSubmit={(e)=>{
                e.preventDefault();
                props.setAlert({visible:true});
                // return false;
            }}> 
                <div class="image_area">
                    <form method="post">
                        <label for="upload_image">
                            <img src={(user==null||user.picture==null)? require("../../../assets/images/emptyFace.png") : props.user.picture} id="uploaded_image" class="img-responsive rounded-circle" width="200px" height="200px" />
                            <div class="overlay">
                                <div class="text">Click to Change Profile Image</div>
                            </div>
                            <input type="file" name="image" class="image" id="upload_image" />
                        </label>
                    </form>
                </div>

                <div class="modal fade" id="modal" tabindex="-1" role="dialog" aria-labelledby="modalLabel" aria-hidden="true">
			  	<div class="modal-dialog modal-lg" role="document">
			    	<div class="modal-content">
			      		<div class="modal-header">
			        		<h5 class="modal-title">Crop Image Before Upload</h5>
			        		<button type="button" class="close" data-dismiss="modal" aria-label="Close">
			          			<span aria-hidden="true">Ã—</span>
			        		</button>
			      		</div>
			      		<div class="modal-body">
			        		<div class="img-container">
			            		<div class="row">
			                		<div class="col-md-8">
			                    		<img src="" id="sample_image" />
			                		</div>
			                		<div class="col-md-4">
			                    		<div class="preview"></div>
			                		</div>
			            		</div>
			        		</div>
			      		</div>
			      		<div class="modal-footer">
			      			<button type="button" id="crop" class="btn btn-primary">Crop</button>
			        		<button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
			      		</div>
			    	</div>
			  	</div>
			</div>  

                {/* <img src={(user==null||user.picture==null)? require("../../../assets/images/emptyFace.png") : props.user.picture} alt="" class="img-thumbnail"></img>
                <div class="form-group">
                    <label>File upload</label>
                    <div class="custom-file">
                        <input lang="es" type="file" id="customFileLang" class="form-control visibility-hidden form-control-file" />
                        <label class="custom-file-label" for="customFileLang">Upload image</label>
                    </div>
                </div> */}
                <div className="form-group">
                    <label for="emailInput">Email Address</label>
                    <input placeholder="Email Address" type="text" id="emailInput" className="form-control form-control-lg" readOnly value={user.email} />
                </div>
                <div className="form-group">
                    <label for="nameInput">Display Name</label>
                    <input placeholder="Display Name" type="email" id="nameInput" className="form-control form-control" value={user.name} />
                </div>
                <div className="form-group">
                    <label for="exampleInputEmail1">Email address</label>
                    <input placeholder="Email" type="email" id="exampleInputEmail1" className="form-control form-control" />
                </div>
                <div className="form-group">
                    <label for="exampleInputPassword1">Password</label>
                    <input placeholder="Password" type="password" id="exampleInputPassword1" className="form-control form-control" />
                </div>
                <div className="form-check">
                    <label className="form-check-label text-muted">
                        <input type="checkbox" className="form-check-input" />
                        <i className="input-helper"></i>Remember me
                    </label>
                </div>
                <button type="submit" className="btn btn-gradient-primary mr-2">Submit</button>
                <button className="btn btn-light">Cancel</button>
            </form>
        </>
    )
}

export default AccountSettings;
