import React, { useEffect, useState } from "react";
import $ from 'jquery';

import { Form } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import Tags from "../../../../node_modules/bootstrap5-tags/tags";
import HobbyList from "./util/hobbyList";

const querystring = require('querystring');

// Language
// Basic: Gender(多元), Date of birth (星座), City & Country (Can set Undefined)
// Introduction questions: 3 (Facts about me)
// Hobby
// Topic you are Interest
// Friends preference: Gender, Age range, Language

const ProfileSettings = (props) => {
    const [user, setUser] = useState(props.user)
    const [email, setEmail] = useState(props.user===null?'':props.user.email)
    const [uname, setUName] = useState(props.user===null?'':props.user.name)
    const [password, setPassword] = useState('')
    const [newPassword, setNewPassword] = useState('')
    const [isLoaded, setLoaded] = useState(false)   // Check if page is loaded

    const [dob, setDOB] = useState('');
    const [hobby, setHobby] = useState('');

    Tags.init("select[multiple]");
    
    // Run when form is changed
    useEffect(()=> {
        if (!isLoaded) {
            setLoaded(true);
            return
        }

        console.log("Form has changed")
        props.setFormChanged(true)
        $("#submitBtn").removeClass('disabled')
    }, [email, uname, password, newPassword]);

    const sendResetPwRequest = async () => {
        let url = '/user/profile/update';

        let res = await fetch(url, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: querystring.stringify({
                name: uname,
                password: password,
                newPassword: newPassword
            })
        });

        let data
        try {
            data = await res.json();
        } catch (error) {
            props.setAlert({
                visible: true,
                strongMsg: 'Error!',
                msg: `Unexpected error. (${error.message})`
            })
            return;
        }

        if (!data.success) {
            props.setAlert({
                visible: true,
                strongMsg: 'Sorry!',
                msg: `${data.message}`
            })
            return;
        }

        setUser(data.user)
        props.setUser(data.user)
        window.alert("Your information is updated")
        resetForm()
    };

    const resetForm = () => {
        $('#submitBtn').addClass('disabled').promise().then("class added")
        setEmail(user.email)
        setUName(user.name)
        setPassword('')
        setNewPassword('')
    }

    return (
        <>
        <form className="forms-sample" onSubmit={async (e)=>{
                e.preventDefault();
                sendResetPwRequest()
                return false;
            }}> 

        <div className="card-body">
            <h4 className="card-title">Profile</h4>
            <p className="card-description">Basic Information</p>

            <div className="row">
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label className="col-sm-3 col-form-label">Language</label>
                    <div className="col-sm-9">
                        <select className="form-control">
                            <option>Cantonese</option>
                            <option>Mandarin</option>
                            <option>English</option>
                        </select>
                    </div>
                    </Form.Group>
                </div>
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label className="col-sm-3 col-form-label">Country of origin</label>
                    <div className="col-sm-9">
                        <select className="form-control form-control-lg">
                            <option>Not Declare</option>
                            <option value="CN">China</option>
                            <option value="HK">Hong Kong</option>
                            <option value="IN">India</option>
                            <option value="ID">Indonesia</option>
                            <option value="JP">Japan</option>
                            <option value="KR">South Korea</option>
                            <option value="MY">Malaysia</option>
                            <option value="PH">Philippines</option>
                            <option value="TW">Taiwan</option>
                            <option value="TH">Thailand</option>
                            <option value="VN">Vietnam</option>
                            <optgroup label="Others">
                                <option value="NA">North America</option>
                                <option value="SA">South America</option>
                                <option value="ER">Europe</option>
                                <option value="AS">Asia</option>
                                <option value="AU">Australia</option>
                                <option value="AF">Africa</option>
                            </optgroup>
                        </select>
                    </div>
                    </Form.Group>
                </div>
            </div>

            <div className="row">
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label className="col-sm-3 col-form-label">Gender</label>
                    <div className="col-sm-9">
                        <select className="form-control form-control-lg">
                            <option>Male</option>
                            <option>Female</option>
                            <option>Trans male</option>
                            <option>Trans female</option>
                            <option>Non-binary</option>
                            <option selected>Not Declare</option>
                        </select>
                    </div>
                    </Form.Group>
                </div>
                <div className="col-md-6">
                    <Form.Group className="row">
                    <label className="col-sm-3 col-form-label">Birth</label>
                    <div className="col-sm-9">
                    <DatePicker className="form-control form-control-lg" size="lg"
                        selected={dob}
                        onChange={setDOB}
                    />
                    </div>
                    </Form.Group>
                </div>
            </div>
        </div>

        <div className="card-body">
            <p className="card-description">Tell us more about you</p>
            <div className="form-group row">
                <label for="emailInput" className="col-sm-3 col-form-label">Hobby</label>
                <div className="col-sm-9">
                    <select
                        class="form-select"
                        name="tags_json[]"
                        multiple
                        data-allow-new="true"
                        data-allow-clear="1"
                        data-server="../../../assets/demo.json"
                        data-live-server="1"
                        >
                        <HobbyList />
                    </select>

                </div>
            </div>
        </div>


        <div className="card-body">
            <p className="card-description">What types of people you would like to meet?</p>

            <div class="text-right">
            <button type="submit" id="submitBtn" className="btn btn-gradient-primary mr-2 disabled" onClick={(e)=>{
                if (e.target.classList.contains('disabled'))
                    e.preventDefault();
            }}>Save Change</button>
            <button type="reset" className="btn btn-light" onClick={(e)=>{e.preventDefault(); resetForm();}}>Reset</button>
            </div>
            
        </div>
        </form>
        </>
    )
}

export default ProfileSettings;
