import React from "react";
import { Form } from 'react-bootstrap';

import './InputBar.css';

function InputBar(props) {
    return (
        <Form className="form-sample">
            <Form.Group>
            <div className="row align-items-center">
                <Form.Control
                    type="text"
                    className="input-box form-control col-sm-8 col-10"
                    placeholder="Type a message..."
                    aria-label="Type a message..."
                    aria-describedby="basic-addon2"
                    value={props.message}
                    onChange={(event) => props.setMessage(event.target.value)}
                    onKeyPress={event => event.key === 'Enter' ? props.sendMessage(event) : null}
                />

                <button className="btn btn-rounded btn-gradient-danger col-sm-2 col-auto" onClick={e => {
                    //props.setMessages([...messages, message])
                    props.sendMessage(e);
                }}>Send</button>
            </div>
                
            </Form.Group>

            {/*
            <input
                className=""
                type="text"
                placeholder="Type a message..."
                value={props.message}
                onChange={(event) => props.setMessage(event.target.value)}
                onKeyPress={event => event.key === 'Enter' ? props.sendMessage(event) : null}
            />
            <button className="btn btn-rounded btn-gradient-danger" onClick={e => {
                //props.setMessages([...messages, message])
                props.sendMessage(e);
            }}>Send</button>
            */}

        </Form>
    )
}

export default InputBar;
