import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import RegisterBox from "../component/register/RegisterBox";
import RegisterSuccess from "../component/register/RegisterSuccess";
import RegisterVerified from '../component/register/RegisterVerified';
import AlertMessage from '../component/common/AlertMessage';
const querystring = require('querystring');

const Register = () => {
  let urlData = window.location.search;
  let queryData = new URLSearchParams(urlData);
  let m = queryData.get('m');
  let id = queryData.get('id') + '1';
  let verify = (m != null && id != null)? true: false;
  let verifySuccess = true;

  if (verify) {
    let url = '/user/activate' + urlData
    let xmlHttp = new XMLHttpRequest();

    let data;
    try {
      xmlHttp.open("GET", url, true);
      xmlHttp.send();
      data = xmlHttp.responseText;
    } catch (error) {
      verifySuccess = false;
      console("Error")
    }

    console.log("Data:" + data);
  }

  const [alert, setAlert] = useState('');
  const [registerSuccess, setRegisterSuccess] = useState('');

    return (
      <div>
        <div className="d-flex align-items-center auth px-0 make-friends-background">
          <div className="row w-100 mx-0">
            <div className="col-lg-4 mx-auto">
              {alert.visible === true && <AlertMessage strongMsg={alert.strongMsg} msg={alert.msg} />}
              <div className="auth-form-light text-left py-5 px-4 px-sm-5">
                
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                {verify && <RegisterVerified />}
                {!verify && !registerSuccess && <RegisterBox setAlert={setAlert} setRegisterSuccess={setRegisterSuccess} />}
                {!verify && registerSuccess && <RegisterSuccess />}
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Register
