import React, { useEffect, useState } from 'react';
import { useHistory, Link } from "react-router-dom";
import { Form } from 'react-bootstrap';
import LoginBox from "../component/login/LoginBox";
import LoginByPassword from "../component/login/LoginByPassword";
import LoginVerify from "../component/login/LoginVerify";
import Signup from "../component/login/Signup";

const Login = () => {
    const [loginState, setloginState] = useState('login');
    // const [loginState, setloginState] = useState('verify');
    const [userName, setUserName] = useState('');
    const [userPassword, setUserPassword] = useState('');

    let navigate = useHistory();

    useEffect(() => {
        console.log(`changed to ${loginState}`)
        if (loginState === 'success') {
            navigate("/home");
        }
    }, [loginState]);

    const sendVerifyEmail = async () => {
      let url = '/user/login/email';
      let res = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
            "email": userName
        })
      });
      let data = await res.json();
      console.log(data);
      if (data.success) {
          // Verification email is sent
      }
    }

    const sendLoginRequest = async () => {
        let url = '/user/login';
        let res = await fetch(url, {
            method: 'POST',
            body: JSON.stringify({
                "email": userName,
                "password": userPassword
            })
        });
        let data = await res.json();
        console.log(data);
        if (data.success) {
            // setloginState('verify');
            setloginState('success');
        }
    };

    let loginplaceholder = 0;
    if (loginState === 'login') {
        loginplaceholder = <LoginBox setUserName={setUserName} setloginState={setloginState} />;
    }

    if (loginState === 'password') {
      console.log(userName)
      loginplaceholder = <LoginByPassword userName={userName} setUserPassword={setUserPassword} sendLoginRequest={sendLoginRequest} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail} />;
    }

    if (loginState === 'verify') {
        loginplaceholder = <LoginVerify userName={userName} setloginState={setloginState} sendVerifyEmail={sendVerifyEmail}/>;
    }

    if (loginState === 'signup') {
        loginplaceholder = <Signup setloginState={setloginState} />;
    }

    return (
      <div>
<div class="alert alert-success alert-dismissible fade show" role="alert">
  <strong>Holy guacamole!</strong> You should check in on some of those fields below.
  <button type="button" class="close" data-dismiss="alert" aria-label="Close">
    <span aria-hidden="true">&times;</span>
  </button>
</div>
        <div className='d-flex align-items-center auth p-5'>
          <div className="row w-100 mx-0">
            <div className="col-md-7 grid-margin">
              <div className="pt-5 pl-5 pr-5">
                <h2>Welcome!</h2>
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>
                <div>Use Frends-HK to meet new friends!</div>
              </div>
            </div>
            <div className="col-md-5 grid-margin stretch-card">
              <div className="card">
                <div className="card-body">
                  <h4 className="card-title">Hello! let's get started</h4>
                  {loginplaceholder}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Login
