import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import RegisterBox from "../component/register/RegisterBox";
import AlertMessage from '../component/common/AlertMessage';

const Register = () => {

  const [userEmail, setUserEmail] = useState('');
  const [userName, setUserName] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [disableInput, setDisableInput] = useState(false);

  const [alert, setAlert] = useState('');

  const sendRegisterRequest = async () => {
    let url = '/user/register';
    let res = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
            email: userEmail,
            name: userName,
            password: userPassword
        })
    });
    let data = await res.json();
    console.log(data);
    setDisableInput(false);
    if (data.success) {
        console.log('register successful');
    }
  };

    return (
      <div>
        <div className="d-flex align-items-center auth px-0 make-friends-background">
          <div className="row w-100 mx-0">
            <div className="col-lg-4 mx-auto">
              <div className="auth-form-light text-left py-5 px-4 px-sm-5">
                { <AlertMessage strongMsg={alert.strongMsg} msg={alert.msg} />}
                <div className="brand-logo">
                  <img src={require("../../assets/images/logo.svg")} alt="logo" />
                </div>

                <RegisterBox />

                <div className="text-center mt-4 font-weight-light">
                  Already have an account? <Link to="/login" className="text-primary">Login</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Register
