import React from 'react';
const querystring = require('querystring');

const LoginVerifier = async (props) => {
    var msgUrl = querystring.stringify({smsg:'', msg: "Please login."})

    if (props.user === null) {
        window.location.pathname = "/login" + msgUrl;
        return
    }

    let myUser = JSON.parse(sessionStorage.getItem('UserProfile'));
    
    let url = '/checkSession';
      let res = await fetch(url, {
          method: 'GET',
      });

      let data;
      try {
          data = await res.json();
      } catch (error) {
            msgUrl = querystring.stringify({smsg:'Error!', msg: `Unexpected error. (${error.message})`})
            window.location.pathname = "/login" + msgUrl;
            return
      }

      const loginVerification = data.verification;
      if (loginVerification) {
          if (loginVerification.email !== myUser.email && loginVerification.verified) {
                msgUrl = querystring.stringify({smsg:'Sorry!', msg: `Session is invalid. Please login again.`})
                window.location.pathname = "/login" + msgUrl;
                return
          }
      }
}

export default LoginVerifier;
