import React, { useState } from "react";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";
// import Cropper from "https://unpkg.com/cropperjs";
// import "https://unpkg.com/cropperjs/dist/cropper.css"

const querystring = require('querystring');

const UploadPicture = (props) => {
    const [user, setUser] = useState(props.user);
    const [userPic, setUserPic] = useState(props.user.picture);

    const [image, setImage] = useState('');
    const [cropData, setCropData] = useState("#");
    const [cropper, setCropper] = useState();
    const [oriFilename, setOriFilename] = useState('');


    var reader;
    const [imageSrc, setImageSrc] = useState("")

    const showModal = () => {
        // cropper = new Cropper(image, {
        //     aspectRatio: 1,
        //     viewMode: 3,
        //     preview:'.preview'
        // });
    }

    const changeImage = (e) => {
        window.document.getElementById("showModalBtn").click();
        e.preventDefault();
        let files;
        if (e.dataTransfer) {
            files = e.dataTransfer.files;
        } else if (e.target) {
            files = e.target.files;
        }
        const reader = new FileReader();
        reader.onload = () => {
            setImage(reader.result);
        }
        reader.readAsDataURL(files[0]);
        console.log(files[0])
        setOriFilename(files[0].name);
    }

    const getCropData = () => {
        if (typeof cropper !== undefined) {
            setCropData(cropper.getCroppedCanvas().toDataURL());
            console.log(cropper)

            let canvas = cropper.getCroppedCanvas();

            canvas.toBlob(function(blob){
                let url = URL.createObjectURL(blob);
                setUserPic(url)

                var reader = new FileReader();
                reader.readAsDataURL(blob);
                reader.onloadend = function(){
                    var base64data = reader.result;

                    // $.ajax({
                    //     url:'upload.php',
                    //     method:'POST',
                    //     data:{image:base64data},
                    //     success:function(data)
                    //     {
                    //         $modal.modal('hide');
                    //         $('#uploaded_image').attr('src', data);
                    //     }
                    // });
                };
            });
        }
    }

    return (
        <>
            <div class="image_area">
                <form>
                <div class="form-group">
                    <label for="upload_image" className="image-wrapper">
                        <img src={(userPic==null)? require("../../../assets/images/emptyFace.png") : userPic} id="uploaded_image" class="img-responsive rounded-circle" width="200px" height="200px" />
                        <div class="overlay">
                            <div class="text">Change Image</div>
                        </div>
                    </label>
                    
                    <div class="custom-file">
                        <input lang="en" type="file" name="image" class="image" id="upload_image" className="form-control visibility-hidden form-control-file" onClick={(e)=>{e.target.value=''; setOriFilename('')}} onChange={changeImage} />
                        <label class="custom-file-label" for="upload_image">{oriFilename===''? 'Upload Image' : oriFilename}</label>
                    </div>
                    

                    
                        
                    </div>
                </form>
            </div>
            

            <div class="modal fade modal-lg" id="modal" tabindex="-1" role="dialog" aria-labelledby="modalLabel" aria-hidden="true">
			  	<div class="modal-dialog modal-dialog-centered" role="document">
			    	<div class="modal-content">
			      		<div class="modal-header">
			        		<h5 class="modal-title">Crop Image Before Upload</h5>
			        		<button type="button" class="close" data-dismiss="modal" aria-label="Close">
			          			<span aria-hidden="true">&times;</span>
			        		</button>
			      		</div>
			      		<div class="modal-body">
			        		<div class="img-container">
			            		<div class="row">
			                		<div class="col-md-8">
                                        <Cropper style={{height: 400, width:600}} aspectRatio={1} preview=".preview" src={image} viewMode={1} guides={true} minCropBoxHeight={10} responsive={true} onInitialized={(instance) => {setCropper(instance)}} />
			                		</div>
			                		<div class="col-md-4">
			                    		<div class="preview"></div>
			                		</div>
			            		</div>
			        		</div>
			      		</div>
			      		<div class="modal-footer">
			      			<button type="button" id="crop" class="btn btn-primary" data-dismiss="modal" onClick={getCropData}>Save</button>
			        		<button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
			      		</div>
			    	</div>
			  	</div>
			</div>
            <input id="showModalBtn" type="hidden" data-toggle="modal" data-target="#modal" onClick={()=>{showModal()}} />
        </>
    )
}

export default UploadPicture;
